mapper.select:
select * from (select * from tb_clazz where isdelete < 1 or isdelete is null) as tb_clazz


    cno = #{cno}


    and name = #{name}

select * from tb_clazz


    cno = #{cno}


    and name = #{name}

mapper.update:
update tb_clazz set number = #{number,jdbcType=INTEGER}, introducation = #{introducation,jdbcType=VARCHAR}, coordinator = #{coordinator,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, telephone = #{telephone,jdbcType=VARCHAR}, gno = #{gno,jdbcType=VARCHAR}, grade_name = #{grade_name,jdbcType=VARCHAR}, isdelete = #{isdelete,jdbcType=BIT}
cno = #{cno,jdbcType=VARCHAR}
and name = #{name,jdbcType=VARCHAR}

update tb_clazz
set isdelete = #{isdelete}
where cno = #{cno,jdbcType=VARCHAR}
and name = #{name,jdbcType=VARCHAR}
